import PropTypes from "prop-types";
import React, { Fragment, useState } from "react";
import { Link } from "react-router-dom";
import { useToasts } from "react-toast-notifications";
import MetaTags from "react-meta-tags";
import { BreadcrumbsItem } from "react-breadcrumbs-dynamic";
import { connect } from "react-redux";
// import { getDiscountPrice } from "../../helpers/product";
// import { useToasts } from "react-toast-notifications";
import {
  addToCart,
  decreaseQuantity,
  deleteFromCart,
  cartItemStock,
  deleteAllFromCart,
} from "../../redux/actions/cartActions";
import LayoutOne from "../../layouts/LayoutOne";
import Breadcrumb from "../../wrappers/breadcrumb/Breadcrumb";
import axios from "axios";
import Cookies from "universal-cookie";
import { useEffect } from "react";
import * as price from "../../data/price.json";

const Cart = ({
  location,
  ci,
  currency,
  decreaseQuantity,
  addToCart,
  deleteFromCart,
  deleteAllFromCart,
}) => {
  const cookies = new Cookies();
  const email = cookies.get("Email") || null;
  const [quantityCount] = useState(1);
  let [cartItems, setCartItems] = useState([]);
  const { addToast } = useToasts();
  const { pathname } = location;
  let cartTotalPrice = 0;
  const data = async () => {
    if (email != null) {
      let response = await axios.get(`http://13.233.238.76/api/cart/${email}`);
      if (response.statusCode === 400) {
        console.log("Handle error");
      } else {
        cartItems = await response.data.arr;
        setCartItems(cartItems);
      }
    } else {
      setCartItems([]);
      return cartItems;
    }
  };
  useEffect(() => {
    data();
  }, []);

  return (
    <Fragment>
      <MetaTags>
        <title>ProPCB | Cart</title>
        <meta name="description" content="Cart page" />
      </MetaTags>

      <BreadcrumbsItem to={process.env.PUBLIC_URL + "/"}>Home</BreadcrumbsItem>
      <BreadcrumbsItem to={process.env.PUBLIC_URL + pathname}>
        Cart
      </BreadcrumbsItem>

      <LayoutOne>
        {/* breadcrumb */}
        <Breadcrumb />
        <div className="cart-main-area pt-90 pb-100">
          <div className="container">
            {cartItems && cartItems.length >= 1 ? (
              <Fragment>
                <h3 className="cart-page-title">Your cart items</h3>
                <div className="row">
                  <div className="col-12">
                    <div className="table-content table-responsive cart-table-content">
                      <table>
                        <thead>
                          <tr>
                            {/* <th>Image</th> */}
                            <th>Product Type</th>
                            <th>Unit PCB Price</th>
                            <th>PCB Qty</th>
                            <th>Subtotal</th>
                            <th>action</th>
                          </tr>
                        </thead>
                        <tbody>
                          {cartItems.map((cartItem, key) => {
                            // const discountedPrice = getDiscountPrice(
                            //   cartItem.price,
                            //   cartItem.discount
                            // );
                            // const finalProductPrice = (
                            //   cartItem.price * currency.currencyRate
                            // ).toFixed(2);
                            // const finalDiscountedPrice = (
                            //   discountedPrice * currency.currencyRate
                            // ).toFixed(2);

                            // discountedPrice != null
                            //   ? (cartTotalPrice +=
                            //       finalDiscountedPrice * cartItem.quantity)
                            //   : (cartTotalPrice +=
                            //       finalProductPrice * cartItem.quantity);
                            cartTotalPrice = cartTotalPrice + cartItem.total;
                            return (
                              <tr key={key}>
                                {/* <td className="product-thumbnail">
                                  <Link
                                    to={
                                      process.env.PUBLIC_URL +
                                      "/product/" +
                                      cartItem.id
                                    }
                                  >
                                    <img
                                      className="img-fluid"
                                      src={
                                        process.env.PUBLIC_URL +
                                        cartItem.image[0]
                                      }
                                      alt=""
                                    />
                                  </Link>
                                </td> */}

                                <td className="product-name">
                                  {/* <Link
                                    to={
                                      process.env.PUBLIC_URL +
                                      "/product/" +
                                      cartItem.id
                                    }
                                  > */}
                                  {cartItem.product_type}
